mapper.select:
select a.*, IFNULL(c.answer_num,0) answer_num from
question a
left join (select b.question_id,count(*) as answer_num from answer b GROUP BY b.question_id) c
on a.id = c.question_id
where a.status!=3

  and a.create_user_id = #{createUserId}


  and a.type = #{type}


  and a.status = #{status}

order by c.answer_num desc, a.create_time

select a.*, IFNULL(c.answer_num,0) answer_num from question a
left join (select b.question_id,count(*) as answer_num from answer b GROUP BY b.question_id) c
on a.id = c.question_id
LEFT JOIN answer d
on d.question_id = a.id
where a.status!=3

  and d.answer_user_id = #{answerUserId}


  and a.type = #{type}


  and a.status = #{status}

order by c.answer_num desc, a.create_time
