mapper.select:
select *
from evaluation_record


        and dimension_id = #{dimensionId,jdbcType=BIGINT}


        and item_id = #{itemId,jdbcType=BIGINT}


        and adding_way = #{addingWay,jdbcType=INTEGER}


        and group_ids = #{groupIds,jdbcType=VARCHAR}


        and FIND_IN_SET(#{studentUserIds,jdbcType=VARCHAR},student_user_ids)


        and score = #{score,jdbcType=DECIMAL}


        and user_id = #{userId,jdbcType=VARCHAR}
