mapper.select:
select * 
from news


    and school_id = #{schoolId}


    and bureau_id = #{bureauId}


    and type_id = #{typeId}


    and status = #{status}


    and topping = #{topping}


order by upload_time desc

select *
from news
where bureau_id = #{bureauId}
and status = 3

  and type_id = #{typeId}

order by topping desc,upload_time desc

mapper.update:
update news
set status = 4
where id = #{id}
