mapper.select:
select sr.* ,ut.teacher_name from user_teacher ut LEFT JOIN ( SELECT sr1.* FROM score_record sr1 LEFT JOIN score_record sr2 on (sr1.user_id = sr2.user_id and sr1.id < sr2.id) WHERE sr2.id is null) sr ON ut.user_id = sr.user_id where ut.bureau_id =#{bureauId}
and ut.school_id = #{schoolId}
ORDER BY sr.score desc

SELECT f.user_name name, f.avatar, p.score
FROM(
SELECT u.user_id, u.user_name ,u.avatar FROM user u left join bureau b on u.corp_id = b.corp_id where b.id = #{bureauId}
) f
LEFT JOIN
(
select sc.user_id,sc.score from score_record as sc where not exists(select 1 from score_record where user_id = sc.user_id and sc.id < id )
) p
on f.user_id = p.user_id
ORDER BY p.score
