mapper.select:
select id,title,pic_id,origin,click_num,upload_time,update_time,type_id,status,topping,bureau_id,school_id,user_id
from supervision


        and school_id = #{schoolId}


        and bureau_id = #{bureauId}


        and type_id = #{typeId}


        and status = #{status}


        and topping = #{topping}


        and user_id = #{userId}


order by upload_time desc

select id,title,pic_id,origin,click_num,upload_time,update_time,type_id,status,topping,bureau_id,school_id
from supervision
where bureau_id = #{bureauId}

and type_id = #{typeId}



        and status = #{status}


        and status <> 0


and school_id is not null
union all
select id,title,pic_id,origin,click_num,upload_time,update_time,type_id,status,topping,bureau_id,school_id
from supervision
where bureau_id = #{bureauId}

    and type_id = #{typeId}


    and status = #{status}

and school_id is null
order by upload_time desc

select id,title,pic_id,origin,click_num,upload_time,update_time,type_id,status,topping,bureau_id,school_id
from supervision
where bureau_id = #{bureauId}
and status = 3

    and type_id = #{typeId}

order by upload_time desc

mapper.update:
update supervision
set status = 4
where id = #{id}

update supervision
set click_num = click_num + 1
where id = #{id}
